package java5i4;

public class sumOfNums {

	public static void sumOfNums(){
		// TODO Auto-generated method stub
		int n=10,sum=0,i;
		for(i=1;i<=n;i++)
		{
			sum=sum+i;
		}
		System.out.println("the sum of first ten natural numbers =" + sum);
		}
	public static void main(String[] args) {
		sumOfNums();
	}

	}


